mapper.select:
select blog_column_id,url
from blog_blogcolumn where
url=#{url}

select blog_column_id,blog_column_name, parent_id, order_num, url, column_type, visible, icon, column_message, create_by, create_time, update_by, update_time, remark from blog_blogcolumn
and parent_id = #{blogColumnId} and visible = #{visible} and column_type = #{columnType}
order by order_num

mapper.update:
update blog_blogcolumn
blog_column_name = #{blogColumnName}, parent_id = #{parentId}, order_num = #{orderNum}, url = #{url}, column_type=#{columnType}, visible = #{visible}, icon = #{icon}, column_message = #{columnMessage}, back_img = #{backImg}, update_by = #{updateBy}, update_time = sysdate(), remark = #{remark}
where blog_column_id = #{blogColumnId}

mapper.delete:
delete from
blog_blogcolumn where blog_column_id = #{value} or parent_id =
#{value}
