mapper.select:
select blog_comment_id,parent_id, show_id,title, reply_name,
be_reply_name,
reply_qq_num, reply_email, reply_head_img,
reply_content, reply_time,like_num,
status, update_by, update_time,
remark from
blog_blogcomment

	 and blog_comment_id = #{blogCommentId}
	 and parent_id = #{parentId}
	 and show_id = #{showId}
	 and reply_name = #{replyName}
	 and be_reply_name = #{beReplyName}
	 and reply_qq_num = #{replyQqNum}
	 and reply_email = #{replyEmail}
	 and reply_head_img = #{replyHeadImg}
	 and reply_content = #{replyContent}
	 and reply_time = #{replyTime}
	 and status = #{status}
	 and update_by = #{updateBy}
	 and update_time = #{updateTime}
	 and remark = #{remark}

order by blog_comment_id desc

mapper.update:
update blog_blogcomment
status = #{status}, update_by = #{updateBy}, update_time = sysdate(), remark = #{remark}
where blog_comment_id = #{blogCommentId}

update blog_blogcomment

	like_num = like_num+1

where blog_comment_id = #{blogCommentId}

mapper.delete:
delete from
blog_blogcomment where blog_comment_id = #{value} or parent_id =
#{value}
