mapper.select:
select blog_keyword_id, keyword_name from blog_blogkeyword
where
keyword_name = #{keywordName}

select blog_keyword_id, keyword_name, keyword_comment_ban,
keyword_message_ban, keyword_suggestion_ban, keyword_replace,
keyword_replace_word_name, status, create_by, create_time, update_by,
update_time, remark from blog_blogkeyword

	 and blog_keyword_id = #{blogKeywordId}
	 and keyword_name = #{keywordName}
	 and keyword_comment_ban = #{keywordCommentBan}
	 and keyword_message_ban = #{keywordMessageBan}
	 and keyword_suggestion_ban = #{keywordSuggestionBan}

	 and keyword_replace = #{keywordReplace}
	 and keyword_replace_word_name =
		#{keywordReplaceWordName}

	 and status = #{status}
	 and create_by = #{createBy}
	 and create_time = #{createTime}
	 and update_by = #{updateBy}
	 and update_time = #{updateTime}
	 and remark = #{remark}

		AND (keyword_name like concat(concat('%',
		#{searchValue}),
		'%') OR keyword_replace_word_name like
		concat(concat('%', #{searchValue}), '%'))

mapper.update:
update blog_blogkeyword
keyword_name = #{keywordName}, keyword_comment_ban = #{keywordCommentBan}, keyword_message_ban = #{keywordMessageBan}, keyword_suggestion_ban = #{keywordSuggestionBan}, keyword_replace = #{keywordReplace}, keyword_replace_word_name = #{keywordReplaceWordName}, status = #{status}, update_by = #{updateBy}, update_time =sysdate(), remark = #{remark}
where blog_keyword_id = #{blogKeywordId}
