mapper.select:
select * from blog_blogmove

	 and blog_move_id = #{blogMoveId}
	 and move_website_id = #{moveWebsiteId}
	 and move_num = #{moveNum}
	 and move_article_editor = #{moveArticleEditor}
	 and move_column = #{moveColumn}
	 and move_blog_status = #{moveBlogStatus}
	 and move_use_original_time = #{moveUseOriginalTime}
	 and move_save_img = #{moveSaveImg}
	 and move_remove_repeat = #{moveRemoveRepeat}
	 and move_message = #{moveMessage}
	 and move_stop_mode = #{moveStopMode}
	 and move_success = #{moveSuccess}
	 and move_success_num = #{moveSuccessNum}
	 and create_by = #{createBy}
	 and create_time = #{createTime}
	 and finish_time = #{finishTime}
	 and take_time = #{takeTime}
	 and remark = #{remark}

mapper.update:
update blog_blogmove
move_message = #{moveMessage}, move_stop_mode = #{moveStopMode}, move_success = #{moveSuccess}, move_success_num = #{moveSuccessNum}, finish_time = #{finishTime}, take_time = #{takeTime}
where blog_move_id = #{blogMoveId}

update blog_blogmove

	move_stop_mode = #{moveStopMode},
	finish_time = #{finishTime},
	take_time = #{takeTime}

where blog_move_id = #{blogMoveId}
