mapper.select:
select blog_tags_id, blog_tags_name from blog_blogtags
where
blog_tags_name = #{blogTagsName}

select blog_tags_id, blog_tags_name, status, tags_message from
blog_blogtags

	 and blog_tags_name = #{blogTagsName}
	 and status = #{status}

select blog_tags_id, blog_tags_name, status, tags_message, back_img,
create_by, create_time, update_by, update_time, remark from
blog_blogtags

	 and blog_tags_name = #{blogTagsName}
	 and status = #{status}

select blog_tags_id, blog_tags_name, status, tags_message, back_img,
create_by, create_time, update_by, update_time, remark from
blog_blogtags

	 and blog_tags_id = #{blogTagsId}
	 and blog_tags_name = #{blogTagsName}
	 and status = #{status}
	 and tags_message = #{tagsMessage}
	 and back_img = #{backImg}
	 and create_by = #{createBy}
	 and create_time = #{createTime}
	 and update_by = #{updateBy}
	 and update_time = #{updateTime}
	 and remark = #{remark}

		AND blog_tags_name like concat(concat('%',
		#{searchValue}), '%')

mapper.update:
update blog_blogtags
blog_tags_name = #{blogTagsName}, status = #{status}, tags_message = #{tagsMessage}, back_img = #{backImg}, update_by = #{updateBy}, update_time = sysdate(), remark = #{remark}
where blog_tags_id = #{blogTagsId}
