mapper.select:
select tool_id,tool_back_id, tool_name, tool_des, tool_type,
tool_code_type,
src_download_status, execexe_download_status,
setupexe_download_status,src_file_name, execexe_file_name,
setupexe_name, status,create_type, create_by, create_time, update_by,
update_time,
remark from commontool_toolmanage

	 and tool_id = #{toolId}
	 and tool_back_id = #{toolBackId}
	 and tool_name = #{toolName}
	 and tool_des = #{toolDes}
	 and tool_type = #{toolType}
	 and tool_code_type = #{toolCodeType}
	 and src_download_status = #{srcDownloadStatus}
	 and execexe_download_status = #{execexeDownloadStatus}

	 and setupexe_download_status = #{setupexeDownloadStatus}

	 and status = #{status}
	 and create_type= #{createType}
	 and create_by = #{createBy}
	 and create_time = #{createTime}
	 and update_by = #{updateBy}
	 and update_time = #{updateTime}
	 and remark = #{remark}

		AND tool_name like concat(concat('%', #{searchValue}),
		'%')

mapper.update:
update commontool_toolmanage
tool_back_id = #{toolBackId}, tool_name = #{toolName}, tool_des = #{toolDes}, src_file_name = #{srcFileName}, execexe_file_name = #{execexeFileName}, setupexe_name = #{setupexeName}, tool_type = #{toolType}, tool_code_type = #{toolCodeType}, src_download_status = #{srcDownloadStatus}, execexe_download_status = #{execexeDownloadStatus}, setupexe_download_status = #{setupexeDownloadStatus}, status = #{status}, update_by = #{updateBy}, update_time = sysdate(), remark = #{remark}
where tool_id = #{toolId}
