mapper.select:
select website_id, website_title_name,website_ico, website_des,
website_domain_name, website_public_ip, website_public_port,
website_copyright_information, website_mail_smtp_url,
website_mail_smtp_port, website_mail_sender_mailbox,
website_mail_sender_nickname, website_mail_sender_password, create_by,
create_time, update_by, update_time, remark from sys_website

	 and website_id = #{websiteId}
	 and website_title_name = #{websiteTitleName}
	 and website_des = #{websiteDes}
	 and website_domain_name = #{websiteDomainName}
	 and website_public_ip = #{websitePublicIp}
	 and website_public_port = #{websitePublicPort}
	 and website_copyright_information =
		#{websiteCopyrightInformation}

	 and website_mail_smtp_url = #{websiteMailSmtpUrl}
	 and website_mail_smtp_port = #{websiteMailSmtpPort}
	 and website_mail_sender_mailbox = #{websiteMailSenderMailbox}

	 and website_mail_sender_nickname =
		#{websiteMailSenderNickname}

	 and website_mail_sender_password =
		#{websiteMailSenderPassword}

	 and create_by = #{createBy}
	 and create_time = #{createTime}
	 and update_by = #{updateBy}
	 and update_time = #{updateTime}
	 and remark = #{remark}

mapper.update:
update sys_website
website_title_name = #{websiteTitleName}, website_ico = #{websiteIco}, website_des = #{websiteDes}, website_domain_name = #{websiteDomainName}, website_public_ip = #{websitePublicIp}, website_public_port = #{websitePublicPort}, website_copyright_information = #{websiteCopyrightInformation}, update_by = #{updateBy}, update_time=sysdate(), remark = #{remark}
where website_id = #{websiteId}

update sys_website
website_mail_smtp_url = #{websiteMailSmtpUrl}, website_mail_smtp_port = #{websiteMailSmtpPort}, website_mail_sender_mailbox = #{websiteMailSenderMailbox}, website_mail_sender_nickname = #{websiteMailSenderNickname}, website_mail_sender_password = #{websiteMailSenderPassword}, update_by = #{updateBy}, update_time=sysdate()
where website_id = #{websiteId}
