mapper.select:
select downloadmanage_id, download_file_name, download_file_uuid_name,
download_file_type, download_file_extension_name,
download_file_size,download_file_url_key,
download_file_url,
download_file_limit_down_num,
download_file_limit_down_time,
download_file_down_num, status,
use_status, create_by, create_time,
update_by, update_time, remark
from tool_downloadmanage

	 and downloadmanage_id = #{downloadmanageId}
	 and download_file_name = #{downloadFileName}
	 and download_file_uuid_name = #{downloadFileUuidName}

	 and download_file_type = #{downloadFileType}
	 and download_file_extension_name =
		#{downloadFileExtensionName}

	 and download_file_size = #{downloadFileSize}
	 and download_file_url_key = #{downloadFileUrlKey}
	 and download_file_url = #{downloadFileUrl}
	 and download_file_limit_down_num = #{downloadFileLimitDownNum}

	 and download_file_limit_down_time =
		#{downloadFileLimitDownTime}

	 and download_file_down_num = #{downloadFileDownNum}
	 and status = #{status}
	 and use_status = #{useStatus}
	 and create_by = #{createBy}
	 and create_time = #{createTime}
	 and update_by = #{updateBy}
	 and update_time = #{updateTime}
	 and remark = #{remark}

		AND download_file_name like concat(concat('%',
		#{searchValue}), '%')

mapper.update:
update tool_downloadmanage
download_file_name = #{downloadFileName}, download_file_uuid_name = #{downloadFileUuidName}, download_file_type = #{downloadFileType}, download_file_extension_name = #{downloadFileExtensionName}, download_file_size = #{downloadFileSize}, download_file_limit_down_num = #{downloadFileLimitDownNum}, download_file_limit_down_time = #{downloadFileLimitDownTime}, status = #{status}, update_by = #{updateBy}, update_time = sysdate(), remark = #{remark}
where downloadmanage_id = #{downloadmanageId}
